        if (data.code == 111) {
          this.x_alert("原密码错误");
        } else if (data.code == 107) {
          this.x_alert("未登录");
        } else if (data.code == 0) {
          window.location.href = "signin.html";
        }
      } else this.x_alert("两次输入的密码不一致");

      return;
    },

