/**
 * 注册界面
 * @file signup.js
 * @author 
 * @copyright 
 * @createDate 2020-12-15
 */

import React from 'react'
import "../signup/signup.css"
import { Link } from 'react-router-dom'
import axios from 'axios'
const base = "/api";
        const teaminfo_model = {
            code:0,
            name: "666",
            list: { seed: "", score: "", last_score: "" },
            rank_score: "",
            match_ranks: [],
            finals_score: "",
            team_docker: { port: 0}
        }
        //1为登录 0为未登录
        let info_model = {
            old: "",
            new: "",
            new_c:"",
        }

        const haslogin_model = 0;
        let filedata = new FormData();
axios({
    data:{
        seedCode:"",
                fileName:"",
                rank:[],
                Aalert:{title:"标题",txt:"我是文本"},
                matches:[],
                finals_rank:[],
                seed_list:[],
                info:info_model,
                teaminfo:teaminfo_model,
                haslogin: haslogin_model,
    },
    async created(){
        this.hasighin();
        this.getseed();
        this.getmatch();
        this.getrank();
        this.get_stage();
    },
    method:{
        async Aget (url) {
            let resbody;
                   await this.$http.get(base + url).then(function (res) {
                        resbody=res.body;
                    }, function (res) {
                        console.log('请求失败' + res);
                    });
            return resbody;
                },
                async Apost(url, db) {
                    //发送 post 请求
            let resbody;
            await this.$http.post(base + url, db, { emulateJSON: true }).then(function (res) {
                     resbody=res.body;
                    }, function (res) {
                        console.log('请求失败' + res);
                    });
            return resbody;
                },
                async resetP() {
            if (this.info.new == this.info.new_c&&this.info.old!=""&&this.info.new!="") {
                let data = await this.Apost('/upload_submit_txt/', this.info);
                if (data.code == 111) {
