            let matchinfo= await this.Aget('/get_match_game_info/'); 
            if (matchinfo.code === 0) {
                return;
                this.matches = matchinfo.matches;
                var matchHTML = "";
                for (var data of matches) {
                    let i = 65;
                    while (data[String.fromCharCode(i)]) {
                        let match = data[String.fromCharCode(i)];
                        ++i;
                        let ulistHTML = "";
                        for (var j = 0; j < 4; ++j) {
                            let ulist = ""
                            if (match.result) {
                                ulist = "<ul>"
                                    + `<li>队名：${match.result[j][4]}</li>`
                                    + `<li>匹配排名：${match.result[j][0]}</li>`
                                    + `<li>分数：${match.result[j][1]}</li>`
                                    + `<li>步数：${match.result[j][2]}</li>`
                                    + `<li>错误提示：${match.result[j][3]}</li>`
                                    + "</ul>";
                            } else {
                                ulist = "<ul>"
                                    + `<li>组名：${match.teams[j]}</li>`
                                    + "</ul>";
                            }
                            ulistHTML += `<td>${ulist}</td>`;
                        }

                        let statusHTML = "";

                        if (!!match.result) {
                            statusHTML = "已完成";
                        } else {
                            statusHTML = match.progress;
                        }

                        matchHTML += "<tr>"
                            + `<td>${match.date}</td>`
                            + `<td>${statusHTML}</td>`
                            + ulistHTML
                            + "</tr>";
                    }
                }
                tbody.innerHTML = matchHTML;
            } else if (matchinfo.code === 100) {
                this.x_alert("比赛信息获取失败");
            }
        },
